import React, { useState } from "react";
import axios from "axios";
import { FaMicrophone, FaPaperPlane } from "react-icons/fa";

export default function Chatbot() {
    const [message, setMessage] = useState("");
    const [chat, setChat] = useState([]);
    const [listening, setListening] = useState(false);

    const sendMessage = async () => {
        if (!message.trim()) return;

        const userMessage = { sender: "user", text: message };
        setChat((prev) => [...prev, userMessage]);
        setMessage("");

        try {
            const { data } = await axios.post("http://localhost:5000/chat", { message });
            setChat((prev) => [...prev, { sender: "bot", text: data.response }]);
        } catch (error) {
            setChat((prev) => [...prev, { sender: "bot", text: "Error getting response." }]);
        }
    };

    const startListening = () => {
        const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
        recognition.lang = "en-US";
        recognition.start();
        setListening(true);

        recognition.onresult = (event) => {
            setMessage(event.results[0][0].transcript);
        };
        recognition.onend = () => setListening(false);
    };

    return (
        <div className="min-h-screen bg-[#121212] text-white flex flex-col items-center justify-center p-4">
            <div className="w-full max-w-3xl flex flex-col h-[80vh] border border-gray-700 rounded-xl overflow-hidden shadow-2xl bg-[#1e1e1e]">
                <div className="flex-1 overflow-y-auto p-6 space-y-4 scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-transparent">
                    {chat.map((msg, i) => (
                        <div key={i} className={`flex ${msg.sender === "user" ? "justify-end" : "justify-start"}`}>
                            <div className={`max-w-[70%] px-4 py-2 rounded-2xl text-sm ${
                                msg.sender === "user"
                                    ? "bg-[#007aff] text-white"
                                    : "bg-[#2a2a2a] text-gray-100"
                            }`}>
                                {msg.text}
                            </div>
                        </div>
                    ))}
                </div>
                <div className="flex items-center gap-3 border-t border-gray-700 p-4 bg-[#181818]">
                    <button
                        onClick={startListening}
                        className={`p-3 rounded-full transition-colors duration-200 ${
                            listening ? "bg-red-600" : "bg-blue-600"
                        }`}
                    >
                        <FaMicrophone className="text-white" />
                    </button>
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Ask about Dubai properties..."
                        className="flex-grow bg-[#2a2a2a] border border-gray-700 rounded-full px-4 py-2 text-white focus:outline-none"
                    />
                    <button
                        onClick={sendMessage}
                        className="p-3 bg-green-600 rounded-full transition hover:bg-green-700"
                    >
                        <FaPaperPlane className="text-white" />
                    </button>
                </div>
            </div>
        </div>
    );
}
